CREATE TABLE `monxin_im_addressee` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `username` varchar(30) NOT NULL COMMENT '用户',
  `addressee` varchar(30) NOT NULL COMMENT '联系人',
