CREATE TABLE `monxin_im_msg_info` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `state` int(1) NOT NULL COMMENT '信息状态 1正常 2已屏蔽',
  `sender` varchar(30) NOT NULL COMMENT '发信人',
  `addressee` varchar(30) NOT NULL COMMENT '收信人',
  `msg_id` bigint(22) DEFAULT '0' COMMENT '信息内容ID',
  `time` bigint(12) NOT NULL COMMENT '发信时间',
  `sender_state` int(1) NOT NULL DEFAULT '1' COMMENT '发人信息状态 1正常',
  `addressee_state` int(1) NOT NULL DEFAULT '1' COMMENT '收人信息状态 1未读 2已读',
  `ajax_show` int(1) NOT NULL DEFAULT '0' COMMENT '是否 AJAX输出过 0否 1是',
  `delete_a` varchar(30) DEFAULT '0' COMMENT '删除者A',
  `delete_b` varchar(30) DEFAULT '0' COMMENT '删除者B',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=332 DEFAULT CHARSET=utf8 COMMENT='信息状态';m;o;n;
