CREATE TABLE `monxin_im_sender_count` (
  `id` int(11) NOT NULL,
  `sender` varchar(30) NOT NULL COMMENT '用户名',
  `day` int(11) NOT NULL DEFAULT '0' COMMENT '24小时发信量',
  `week` int(11) NOT NULL DEFAULT '0' COMMENT '近7天发信量',
  `month` int(11) NOT NULL DEFAULT '0' COMMENT '近30天发信量',
  `blacklist` int(5) NOT NULL DEFAULT '0' COMMENT '被拉黑次数'
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='用户发信统计';m;o;n;
